import React, { useState } from "react";
import "./Header.css";
import MenuIcon from "@mui/icons-material/Menu";
import CancelIcon from "@mui/icons-material/Cancel";

function Header() {
  const [openMenu, setOpenMenu] = useState(false);

  return (
    <div className="head-container">
      <div className="row1"><h3>Receive a complimentary sample of Karst Eau de Parfum and a hand-dyed cotton bag with a purchase of Eleos Aromatique Hand Balm.Discover Eleos</h3>
      </div>
      <div className="row1"><h3>Enjoy complimentary shipping on orders over $400. Click and Collect is now available in Hong Kong.</h3>
      </div>
      <div className="head-contain">
        <div className={openMenu ? "cate-btn active" : "cate-btn"}>
          <h4>New & Notable</h4>
          <h4>Skin Care</h4>
          <h4>Hand & Body</h4>
          <h4>Home</h4>
          <h4>Contact us</h4>
          <h4>Hair</h4>
          <h4>Fragrance</h4>
          <h4>Kits & Travel</h4>
          <h4>Gifts</h4>
          <h4>Read</h4>
          <h4>Stores</h4>
          <h4>Facial Appointments</h4>
          <h4></h4>
          


          <CancelIcon
            className="close-icon"
            onClick={() => {
              setOpenMenu(false);
            }}
          />
          <MenuIcon
            className="menu-icon"
            onClick={() => {
              setOpenMenu(true);
            }}
          />
        </div>
        <div className="right-sec">
        <h4>Login</h4>
        <a href="#"><h4>Cabinet</h4></a>
        <a href="#"><h4>Cart</h4></a>
        </div>
      </div>
    </div>
  );
}

export default Header;
