import React from "react";
import "./SectionFirst.css";
import Slider from "react-slick";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

function SectionFirst() {

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  
  return (
    <div className="sectionFirst">
      <Slider {...settings}>
        <div className="slide1">
          <div className="col1">
            <img src="../../../../public/logopng.png" alt="" />
          </div>
          <div className="col2">
            <h4>Limited-edition</h4>
            <h1>A colourful fragrant pairing</h1>
            <p>
              Receive a complimentary sample of Karst Eau de Parfum and a
              hand-dyed cotton bag with a purchase of Eleos Aromatique Hand
              Balm, while available.
            </p>
            <a href="" className="btn">
              <p>Discover Eleos</p>
              <ArrowForwardIcon />
            </a>
          </div>
        </div>
      </Slider>
    </div>
  );
}

export default SectionFirst;
